const burgerMenu=document.querySelector(".burger"),bodyElement=document.body,navMenu=document.querySelector(".header__nav-menu"),stars=document.querySelectorAll(".star"),accordion=document.querySelector(".promocode__more");function toggleMenu(){burgerMenu.classList.toggle("active"),bodyElement.classList.toggle("overflow"),navMenu.classList.toggle("active")}function setRating(e){for(var t=e.target.getAttribute("data-value"),o=0;o<stars.length;o++)o<t?stars[o].classList.add("active"):stars[o].classList.remove("active")}burgerMenu.addEventListener("click",(function(){toggleMenu()}));for(var i=0;i<stars.length;i++)stars[i].addEventListener("click",setRating);function toggleAccordion(){accordion.classList.toggle("active")}accordion.addEventListener("click",(function(){toggleAccordion()}));